import { CallManager } from './callManager.js';
import { UI_WIDTH } from './constants.js';
import { createMap } from './createMap.js';
import { Debug } from './debug.js';
import { Events } from './events.js';
import { Game } from './game.js';
import { LineManager } from './lineManager.js';
import { PointManager } from './pointManager.js';
import { VehicleManager } from './vehicleManager.js';
export let canvas;
export let context;
export let pointManager;
export let lineManager;
export let events;
export let callManager;
export let vehicleManager;
export let game;
export let debug;
export function setCanvas() {
    canvas = document.querySelector("canvas");
    context = canvas.getContext("2d");
    canvas.width = window.innerWidth - UI_WIDTH;
    canvas.height = window.innerHeight;
    pointManager = new PointManager();
    createMap(pointManager.points);
    lineManager = new LineManager();
    events = new Events();
    callManager = new CallManager();
    vehicleManager = new VehicleManager();
    vehicleManager.spawnDefaultVehicles();
    game = new Game();
    game.loop();
    debug = new Debug();
}
